import React, {useState} from "react";

export default function SearchArea() {
const [city,setCity]=useState("")

  return (
    <div className="search-section">
      <form
        action=""
        className="search-form"
      >
        <span>Search</span>
        <input type="text" placeholder="Enter a city name" className="search-input" onChange={(e)=>setCity(e.target.value)} required/>
      </form>
      <button className="search button" onClick={fetchWeather}> <i className="fas fa-search"></i> Search</button>
      <button className="location button"> <i className="fas fa-location-arrow"></i> My Location</button>
    </div>
  );
}
