import React, { useState, useContext } from "react";
import WeatherDisplay from "./HourlyForecast";
import WeatherContext from "./WeatherContext";
import { ErrorBoundary } from "react-error-boundary";

const FallbackComponent = () => (
  <p style={{ color: "red" }}>Something went wrong!</p>
);

export default function SearchArea() {
  const [city, setCity] = useState("");

  const { updateWeather, updateError } = useContext(WeatherContext);

  const fetchWeather = async () => {
    // const API_Key = "4767c92c7510a82fd1314991672cdbba";
    // const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_Key}&units=metric`;

    try {
      const response = await fetch(url);
      const data = await response.json();
      if (response.ok) {
        updateWeather(data);
      } else {
        throw new Error(data.message || "Failed to fetch weather data");
      }
    } catch (error) {
      updateError(error.message); // Set error message
      console.log("Error fetching weather data:", error);
    }
  };

  const fetchWeatherByLocation = async (lat, lon) => {
    const API_Key = "4767c92c7510a82fd1314991672cdbba";
    const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_Key}&units=metric`;

    try {
      const response = await fetch(url);
      const data = await response.json();

      if (response.ok) {
        updateWeather(data);
      } else {
        throw new Error("Failed to fetch weather data");
      }
    } catch (error) {
      updateError(error.message);
      console.log("Error fetching weather data:", error);
    }
  };

  const getLocationAndFetchWeather = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          fetchWeatherByLocation(latitude, longitude);
        },
        (error) => {
          updateError(error.message);
          console.log("Error retrieving your location:", error);
        }
      );
    } else {
      console.log("Geolocation is not supported by this browser.");
    }
  };

  return (
    <div className="search-section">
      <form action="" className="search-form">
        <span>Search</span>
        <input
          type="text"
          value={city}
          placeholder="Enter a city name"
          className="search-input"
          onChange={(e) => setCity(e.target.value)}
          required
        />
      </form>
      <button className="search button" onClick={fetchWeather}>
        {" "}
        <i className="fas fa-search"></i> Search
      </button>
      <button className="location button" onClick={getLocationAndFetchWeather}>
        {" "}
        <i className="fas fa-location-arrow"></i> My Location
      </button>
      <ErrorBoundary>
        <WeatherDisplay />
      </ErrorBoundary>
    </div>
  );
}
