import React , {useContext} from "react";
import WeatherContext from "./WeatherContext";


export default function WeatherDisplay({weather, error}){
    if (error){
        return <p style={{color:"red"}}>{error}</p>
    }

    return(
        <div className="displayWeather">
            <h3>{weather.name}</h3>
            <p></p>
        </div>
    )
}
